-- Basic nearest neighbour approach
DECLARE @Fire geometry;
SET @Fire = geometry::STPointFromText('POINT (210000 890000)', 26986);
SELECT TOP 1
  Name,
  Address,
  City,
  Location.STDistance(@Fire) AS Distance
FROM
  MA_Firestations
ORDER BY
  Location.STDistance(@Fire) ASC;
GO

