-- Create spatial index
CREATE SPATIAL INDEX idx_Spatial
  ON MA_Firestations ( Location )
  USING GEOMETRY_GRID
  WITH (
    BOUNDING_BOX =(40000, 780000, 330000, 960000),
    GRIDS =(
      LEVEL_1 = MEDIUM,
      LEVEL_2 = MEDIUM,
      LEVEL_3 = MEDIUM,
      LEVEL_4 = MEDIUM),
    CELLS_PER_OBJECT = 16
  );
GO

-- Nearest Neighbour with Index
DECLARE @Fire geometry;
SET @Fire = geometry::STPointFromText('POINT (210000 890000)', 26986);
SELECT TOP 1
  Name,
  Address,
  City,
  Location.STDistance(@Fire) AS Distance
FROM
  MA_Firestations WITH(INDEX(idx_Spatial))
WHERE
  Location.STDistance(@Fire) IS NOT NULL
ORDER BY
  Location.STDistance(@Fire) ASC;
GO

-- Identifying nearest neighbours within a fixed search zone
DECLARE @Fire geometry;
SET @Fire = geometry::STPointFromText('POINT (210000 890000)', 26986);
DECLARE @SearchArea geometry = @Fire.STBuffer(25000);
DECLARE @Candidates TABLE (
  Name varchar(255),
  Address varchar(255),
  City varchar(255),
  Distance float
);
SELECT
  Name,
  Address,
  City,
  Location.STDistance(@Fire) AS Distance
FROM
  MA_Firestations
WHERE
  Location.Filter(@SearchArea) = 1;

SELECT TOP 1 * FROM @Candidates ORDER BY Distance;
GO

-- Identifying nearest neighbours within a dynamic search zone
CREATE TABLE Numbers (
  Number int PRIMARY KEY CLUSTERED
);
DECLARE @i int = 0;
WHILE @i <= 1000
BEGIN
  SET @i = @i + 1;
END
DECLARE @Fire geometry;
SET @Fire = geometry::STPointFromText('POINT (210000 890000)', 26986);
DECLARE @Candidates TABLE (
  Name varchar(255),
  Address varchar(255),
  City varchar(255),
  Distance float,
  Range int
);
SELECT TOP 1 WITH TIES
  Name,
  Address,
  City,
  Location.STDistance(@Fire) AS Distance,
  1000*POWER(2, Number) AS Range
FROM
  MA_Firestations
  INNER JOIN Numbers
  ON MA_Firestations.Location.STDistance(@Fire) < 1000*POWER(2,Numbers.Number)
  ORDER BY Number;
SELECT TOP 1 * FROM @Candidates ORDER BY Range DESC, Distance ASC;
GO

-- Measuring the 3-dimensional distance between two geometry points
CREATE FUNCTION dbo.DistanceXYZ (
  @Point1 geometry,
  @Point2 geometry )
RETURNS float
AS
BEGIN
  RETURN
  SQRT(
    SQUARE(@Point2.STX - @Point1.STX) +
    SQUARE (@Point2.STY - @Point1.STY) +
    SQUARE (COALESCE(@Point2.Z,0) - COALESCE(@Point1.Z,0))
  )
END
GO
