-- Perform a quick test of intersection using Filter()
CREATE TABLE #Geometries (
  id int IDENTITY(1,1) PRIMARY KEY,
  geom geometry
  )
CREATE SPATIAL INDEX [idx_Spatial]
  ON [dbo].[#Geometries] ( geom )
  USING GEOMETRY_GRID
  WITH (
    BOUNDING_BOX =(-180, -90, 180, 90),
    GRIDS =(
      LEVEL_1 = MEDIUM,
      LEVEL_2 = MEDIUM,
      LEVEL_3 = MEDIUM,
      LEVEL_4 = MEDIUM),
    CELLS_PER_OBJECT = 4 )
 'POLYGON((52.09 -2.14, 51.88 -2.15, 51.89 -1.89,52.12 -1.99, 52.09 -2.14))', 0)),
(geometry::STPolyFromText(
 'POLYGON((52.1 -2, 52.05 -2.01, 51.9 -1.9, 52.11 -2.15, 52.15 -1.9, 52.1 -2))', 0))
DECLARE @Point geometry = geometry::STGeomFromText('POINT(52.07 -2)', 0)

-- Determine which geometries intersect using primary Filter() only
SELECT id
FROM #Geometries
WITH(INDEX(idx_Spatial))
WHERE geom.Filter(@Point) = 1;

-- Determine which geometries intersect using STIntersects()
SELECT
id
FROM #Geometries
WITH(INDEX(idx_Spatial))
WHERE geom.STIntersects(@Point) = 1;


